package test;

public class Circle {

	private double x;
	private double y;	
	private double radius;
	
	public Circle() {
		x = y = 1;
		radius = 1;
		
	}
	public Circle(double x, double y, double radius) {
		this.x = x;
		this.y = y;
		this.radius = radius;
		// TODO Auto-generated constructor stub
	}

	public double getRadius() {
		return radius;
	}

	public void setRadius(double radius) {
		this.radius = radius;
	}




}
